import Vue from 'vue'
import VueRouter from 'vue-router'
import Home from '../views/Home.vue'
import Packages from '../views/Packages.vue'
import Agent from '../views/Agent.vue'
import Gallery from '../views/Gallery.vue'
import Login from '../views/Login.vue'

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home
  },
  {
    path: '/packages',
    name: 'Packages',
    component: Packages
  },
  {
    path: '/agent',
    name: 'Agent',
    component: Agent
  },
  {
    path: '/gallery',
    name: 'Gallery',
    component: Gallery
  },
  {
    path: '/login',
    name: 'Login',
    component: Login,
    meta: {
      guest: true
    }
  }
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

// 路由守卫
router.beforeEach((to, from, next) => {
  const isAuthenticated = localStorage.getItem('user') // 临时认证检查

  if (to.matched.some(record => !record.meta.guest)) {
    // 需要认证的路由
    if (!isAuthenticated) {
      next('/login')
    } else {
      next()
    }
  } else {
    // 游客可访问的路由
    if (isAuthenticated && to.path === '/login') {
      next('/')
    } else {
      next()
    }
  }
})

export default router
